pragma solidity 0.8.7;


contract RoninStake is ERC20Burnable, ERC20Capped, AccessControlEnumerable, ERC721Holder
{
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.UintSet;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint256 public constant TOKENS_PER_POINT = 1e18; // 1 tokens per point
    IERC721 public ronin;

    // ronin token id => wallet address
    mapping(uint256 => address) public owners;

    // wallet address => [ronin token id]
    mapping(address => EnumerableSet.UintSet) private ownerInfo;

    modifier onlyMinter {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not a minter");
        _;
    }

    modifier onlyAdmin {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not an admin");
        _;
    }

    event Stake(
        address indexed user,
        uint256 indexed tokenId
    );

    event Unstake(
        address indexed user,
        uint256 indexed tokenId
    );

    constructor(address _admin, address _ronin) 
        ERC20("Ronin Stake", "RONINs")
        ERC20Capped(100000000e18) // 100M capped
    {
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);
        ronin = IERC721(_ronin);
    }

    // function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Receiver, AccessControlEnumerable) returns (bool) {
    //     return super.supportsInterface(interfaceId);
    // }
    function calculateTokenAmount(uint256 tokenId) 
        public view returns (uint256 amount)
    {
        amount = 10e18;
    }

    function stake(uint256 tokenId) external {
        require(ronin.ownerOf(tokenId) == msg.sender, "permission denied");        
        owners[tokenId] = msg.sender;
        ownerInfo[msg.sender].add(tokenId);
        ronin.safeTransferFrom(msg.sender, address(this), tokenId);
        _mint(msg.sender, calculateTokenAmount(tokenId));
        emit Stake(msg.sender, tokenId);
    }

    function unstake(uint256 tokenId) external {
        require(owners[tokenId] == msg.sender, "permission denied");
        owners[tokenId] = address(0);
        ownerInfo[msg.sender].remove(tokenId);
        ronin.safeTransferFrom(address(this), msg.sender, tokenId) ;        
        _burn(msg.sender, calculateTokenAmount(tokenId));
        emit Unstake(msg.sender, tokenId);
    }

    function ownerInfoCount(address owner) 
        external view returns (uint256)
    {
        return ownerInfo[owner].length();
    }

    function ownerInfoAtIndex(address owner, uint256 index)
        external view returns (uint256)
    {
        return ownerInfo[owner].at(index);
    }

    function _mint(address account, uint256 amount) 
        internal virtual override (ERC20, ERC20Capped) {
        super._mint(account, amount);
    }

    function mint(address receiver, uint256 amount) external onlyMinter {
        _mint(receiver, amount);
    }

    function grantMinter(address minter) external onlyAdmin {
        grantRole(MINTER_ROLE, minter);
    }

    function revokeMinter(address minter) external onlyAdmin {
        revokeRole(MINTER_ROLE, minter);
    }
