pragma solidity ^0.8.0;

import "./openzeppelin/v4/access/AccessControlEnumerable.sol";
import "./openzeppelin/v4/utils/structs/EnumerableSet.sol";


contract Voting is AccessControlEnumerable {

    using EnumerableSet for EnumerableSet.UintSet;

    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");


    struct Choice {
        uint id;
        string name;
        uint votes;
    }

    struct Ballot {
        uint id;
        string name;
        address owner;
        bool isKyc;
        bool isChoiceAddable;
        Choice[] choices;
        uint start;
        uint end;
        uint ballotType;
    }


    mapping(uint => Ballot) public ballots;
    mapping(uint => mapping(address => bool)) whitelistedvoters;
    mapping(address => mapping(uint => bool)) public votes;
    mapping(address => mapping(uint => bool)) public addchoices;

    event CreateBallot(uint BallotId , uint BallotType);

    uint nextBallotId;

    mapping(address => EnumerableSet.UintSet) private creatorBallotIds;

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(CREATOR_ROLE, msg.sender);
    }

    function createBallot(
        string memory name,
        bool isKyc,
        string[] calldata _choices,
        uint start,
        uint duration,
        uint ballotType
    )
        onlyRole(CREATOR_ROLE)
        external
    {

      ballots[nextBallotId].id = nextBallotId; 
      ballots[nextBallotId].name = name;
      ballots[nextBallotId].owner = msg.sender;
      ballots[nextBallotId].isKyc = isKyc;
      ballots[nextBallotId].ballotType = ballotType;
      ballots[nextBallotId].start = start;
      ballots[nextBallotId].end = start + duration;

    if (ballots[nextBallotId].ballotType == 0) {
        ballots[nextBallotId].choices.push(Choice(0, 'True', 0));
        ballots[nextBallotId].choices.push(Choice(1, 'False', 0));
        ballots[nextBallotId].isChoiceAddable = false;


    }

    else if (ballots[nextBallotId].ballotType == 1) {
        for(uint i = 0; i < _choices.length ; i++) {
        ballots[nextBallotId].choices.push(Choice(i, _choices[i], 0));
        ballots[nextBallotId].isChoiceAddable = false;

      }
    }

    else if (ballots[nextBallotId].ballotType == 2) {
        ballots[nextBallotId].isChoiceAddable = true;

      }
    

      creatorBallotIds[msg.sender].add(nextBallotId);

     
      emit CreateBallot(nextBallotId ,ballots[nextBallotId].ballotType );

      nextBallotId++;
    }

    function deleteBallot(
        uint ballotId
    )
        onlyRole(CREATOR_ROLE)
        external
    {
        require(ballots[ballotId].owner == msg.sender, 'only owner can delete ballot');

        delete ballots[ballotId];
        creatorBallotIds[msg.sender].remove(ballotId);

    }


    function addWhitelistedVoters(
        uint ballotId,
        address[] calldata _voters
    )
        onlyRole(CREATOR_ROLE)
        external
    {
        require(ballots[ballotId].isKyc == true, 'only kyc ballot can configure as whitelist');
        require(ballots[ballotId].owner == msg.sender, 'only owner can delete ballot');
        
        for(uint i = 0; i < _voters.length; i++) {
            whitelistedvoters[ballotId][_voters[i]] = true;
        }

    }

     function isWhitelisted(
        address user,
        uint ballotId
    )
        external
        view
        returns (bool)
    {
        return
            whitelistedvoters[ballotId][user];
    }

    function vote(
        uint ballotId,
        uint choiceId
    )
        external
    {
        if (ballots[ballotId].isKyc) {
            require(whitelistedvoters[ballotId][msg.sender] == true, 'only whitelisted voters can vote');
        } 

        require(votes[msg.sender][ballotId] == false, 'voter can only vote once for a ballot');
        require(block.timestamp < ballots[ballotId].end && block.timestamp > ballots[ballotId].start, 'can only vote until ballot end date');
        votes[msg.sender][ballotId] = true;
        ballots[ballotId].choices[choiceId].votes++;
    }

    function addChoice(
        uint ballotId,
        string memory choice
    )
        external
    {

        if (ballots[ballotId].isKyc) {
            require(whitelistedvoters[ballotId][msg.sender] == true, 'only whitelisted voters can add');
        } 

        require(ballots[ballotId].isChoiceAddable == true, 'only ballot with choice addable can configured');
        require(addchoices[msg.sender][ballotId] == false, 'voter can only add choice once for a ballot');
        require(block.timestamp < ballots[ballotId].end && block.timestamp > ballots[ballotId].start, 'can only vote until ballot end date');

        uint256 nextChoiceId = getChoicesLength(ballotId);

        addchoices[msg.sender][ballotId] = true;

        ballots[ballotId].choices.push(Choice(nextChoiceId, choice, 1));

    }

    function results(
        uint ballotId
    ) 
        view 
        external 
        returns(Choice[] memory)
    {
        //require(block.timestamp >= ballots[ballotId].end, 'cannot see the ballot result before ballot end');
        return ballots[ballotId].choices;
    }

    function getCreatorBallotIds()
        external
        view
        returns (uint256[] memory)
    {
        return
            creatorBallotIds[msg.sender].values();
    }

    function getBallotIdsByAddress(
        address creator
    )
        external
        view
        returns (uint256[] memory)
    {
        return
            creatorBallotIds[creator].values();
    }

    function getChoicesLength(
        uint ballotId
    )
        internal
        view
        returns (uint256)
    {
        return ballots[ballotId].choices.length;
    }
  

}","keccak256":"0x567c7b9466e920cd53e864348f6c7baaae054c0184d48a9dfc3a9854a2796925","license":"MIT"},"contracts/openzeppelin/v4/access/AccessControl.sol":{"content":"// SPDX-License-Identifier: MIT
